    [Parameter]
    public int Id { get; set; }

    public Family Family { get; set; }
    private IList<Adult> AdultsInFamily;
    private IList<Child> ChildrenInFamily;
    private IList<Pet> PetsInFamily;
    private String memberType;
    private int memberId;
    public Adult Adult;
    public Child Child;
    public Pet Pet;
    String errorMessage;

    protected override async Task OnInitializedAsync()
    {
        if (Id != 0)
        {
            Family = FamilyService.GetFamilies().First(f => f.Id == Id);
        }
        AdultsInFamily = FamilyService.GetAdultsInFamily(Family);
        ChildrenInFamily = FamilyService.GetChildrenInFamily(Family);
        PetsInFamily = FamilyService.GetPetsInFamily(Family);
        memberType = "";
        errorMessage = "";
    }

    private void AddAdult()
    {
        if (AdultsInFamily.Count >= 2)
        {
            errorMessage = "This family already has 2 adults. You cannot add more....";
        }
        else
        {
            NavigationManager.NavigateTo("AddAdult/" + Family.Id);
        }
    }

    private void AddChild()
    {
        if (ChildrenInFamily.Count >= 7)
        {
            errorMessage = "This family already has 7 children. You cannot add more....";
        }
        else
        {
            NavigationManager.NavigateTo("AddChild/" + Family.Id);
        }
    }

    private void AddPet()
    {
        NavigationManager.NavigateTo("AddPet/" + Family.Id);
    }

    private void ShowMember(String type, int id)
    {
        if (type.Equals("adult"))
        {
            Adult = MemberService.GetAdults().Where(a => a.Id == id).ToList()[0];
        }
        else if (type.Equals("child"))
        {
            Child = MemberService.GetChildren().Where(c => c.Id == id).ToList()[0];
        }
        else
        {
            Pet = MemberService.GetPets().Where(p => p.Id == id).ToList()[0];
        }

        memberType = type;
        memberId = Id;
    }

    private void RemoveFamilyWithMembers(Family family)
    {
        FamilyService.RemoveFamily(family);
        MemberService.RemoveAllFamilyMembers(family);
        NavigationManager.NavigateTo("FindAddFamily");
    }
    
    private void RemoveFamily(Family family)
    {
        FamilyService.RemoveFamily(family);
        NavigationManager.NavigateTo("FindAddFamily");
    }

