    private void ShowMember(String type, int id)
    {
        if (type.Equals("adult"))
        {
            Adult = MemberService.GetAdults().Where(a => a.Id == id).ToList()[0];
        }
        else if (type.Equals("child"))
        {
            Child = MemberService.GetChildren().Where(c => c.Id == id).ToList()[0];
        }
        else
        {
            Pet = MemberService.GetPets().Where(p => p.Id == id).ToList()[0];
        }

        memberType = type;
        memberId = Id;
    }

    private void RemoveFamilyWithMembers(Family family)
    {
        FamilyService.RemoveFamily(family);
        MemberService.RemoveAllFamilyMembers(family);
        NavigationManager.NavigateTo("FindAddFamily");
    }
    
    private void RemoveFamily(Family family)
    {
        FamilyService.RemoveFamily(family);
        NavigationManager.NavigateTo("FindAddFamily");
    }

